package day35.RestaurantTask;public class Server {    private String name;    private int employeeId;    private double hourlyRate;    private boolean isFullTime;    public Server(String name, int employeeId, double hourlyRate, boolean isFullTime) {        setName(name);        setEmployeeId(employeeId);        setHourlyRate(hourlyRate);        setFullTime(isFullTime);    }    public String getName() {        return name;    }    public void setName(String name) {        if (name.isEmpty() || name.isBlank()) {            System.err.println("Invalid name");            System.exit(1);        }        this.name = name;    }    public int getEmployeeId() {        return employeeId;    }    public void setEmployeeId(int employeeId) {        if (employeeId <= 0) {            System.out.println("Invalid employeeID" + employeeId);            System.exit(0);        }        this.employeeId = employeeId;    }    public double getHourlyRate() {        return hourlyRate;    }    public void setHourlyRate(double hourlyRate) {        this.hourlyRate = hourlyRate;    }    public boolean isFullTime() {        return isFullTime;    }    public void setFullTime(boolean fullTime) {        isFullTime = fullTime;    }    public String toString() {        return "Server{" +                "name='" + name + '\'' +                ", employeeId=" + employeeId +                ", hourlyRate=" + hourlyRate +                ", isFullTime=" + isFullTime +                '}';    }}/*6.1  Create a class called Server	        Attributes:	                name (String), employeeID (int), hourlyRate (double), fullTime (boolean)Add A constructor that can set all the fields	        Actions: (all void methods)	            takeOrder(): server's name + "is taking an order"	            cleanTable(): server's name + "is cleaning the table"	            toString(): Returns (String) all the information of a Server	                -> Extra: For fullTime status, instead of printing a boolean value, print "full-tim*/