package day35.ScrumTask;public class Developer {    private String name, jobTitle;    private int employeeID;    private double salary;    public Developer(String name, String jobTitle, int employeeID, double salary) {        setName(name);        setJobTitle(jobTitle);        setEmployeeID(employeeID);        setSalary(salary);    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getJobTitle() {        return jobTitle;    }    public void setJobTitle(String jobTitle) {        this.jobTitle = jobTitle;    }    public int getEmployeeID() {        return employeeID;    }    public void setEmployeeID(int employeeID) {        if (employeeID <= 0) {            System.out.println("Invalid ID number:" + employeeID);            System.exit(0);        }        this.employeeID = employeeID;    }    public double getSalary() {        return salary;    }    public void setSalary(double salary) {        if (salary < 0) {            System.out.println("Invalid salary:" + salary);            System.exit(0);        }        this.salary = salary;    }    public String toString() {        return "Developer{" +                "name='" + name + '\'' +                ", jobTitle='" + jobTitle + '\'' +                ", employeeID=" + employeeID +                ", salary=" + salary +                '}';    }}/*create a class called Developer	          Attributes:	            name, employeeID, JobTitle, Salary	          Add A constructor that can set all the fileds	          Actions:	            coding(), unitTesting(), fixingBug(), toString()*/