package day35.ScrumTask;import java.util.ArrayList;public class Tester {    private String name, jobTitle;    private int employeeID, salary;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getJobTitle() {        return jobTitle;    }    public void setJobTitle(String jobTitle) {        this.jobTitle = jobTitle;    }    public int getEmployeeID() {        return employeeID;    }    public void setEmployeeID(int employeeID) {        if (employeeID <= 0) {            System.out.println("Invalid Employee ID");            System.exit(0);        }        this.employeeID = employeeID;    }    public int getSalary() {        return salary;    }    public void setSalary(int salary) {        if (salary < 0) {            System.out.println("Salary cannot be negative");            System.exit(0);        }        this.salary = salary;    }    public Tester(String name, String jobTitle, int employeeID, int salary) {        setName(name);        setJobTitle(jobTitle);        setEmployeeID(employeeID);        setSalary(salary);    }    public String toString() {        return "Tester{" +                "name='" + name + '\'' +                ", jobTitle='" + jobTitle + '\'' +                ", employeeID=" + employeeID +                ", salary=" + salary +                '}';    }}/*create a class called Tester	        Attributes:	            name, employeeID, JobTitle, Salary	        Add A constructor that can set all the fileds	        Actions:	           smokeTesting(),  creatingTicket(), dailyStandUp() toString()*/