package day35.Task1;public class Pizza {    private String size;    private int numberOfCheeseTopping, numberOfPepperoniTopping;    public String getSize() {        return size;    }    public void setSize(String size) {        if (!(size.equalsIgnoreCase("small") || size.equalsIgnoreCase("medium") || size.equalsIgnoreCase("large")) ){            System.out.println("Invalid size:" + size);            System.exit(0);        }        this.size = size;    }    public int getNumberOfCheeseTopping() {        return numberOfCheeseTopping;    }    public void setNumberOfCheeseTopping(int numberOfCheeseTopping) {        if (size.equalsIgnoreCase("small") && (numberOfCheeseTopping >= 0 || numberOfCheeseTopping < 5)) {            this.numberOfCheeseTopping = numberOfCheeseTopping;        } else if (size.equalsIgnoreCase("medium") && (numberOfCheeseTopping >= 0 || numberOfCheeseTopping < 6)) {            this.numberOfCheeseTopping = numberOfCheeseTopping;        } else if (size.equalsIgnoreCase("large") && (numberOfCheeseTopping >= 0 || numberOfCheeseTopping < 7)) {            this.numberOfCheeseTopping = numberOfCheeseTopping;        } else {            System.out.println("Invalid number of cheese toping:" + numberOfCheeseTopping);            System.exit(0);        }        this.numberOfCheeseTopping = numberOfCheeseTopping;    }    public void getNumberOfPepperoniTopping() {        this.numberOfPepperoniTopping = numberOfPepperoniTopping;    }    public void setNumberOfPepperoniTopping(int numberOfPepperoniTopping) {        if (size.equalsIgnoreCase("small") && (numberOfPepperoniTopping >= 0 || numberOfPepperoniTopping < 5)) {            this.numberOfPepperoniTopping = numberOfPepperoniTopping;        } else if (size.equalsIgnoreCase("medium") && (numberOfPepperoniTopping >= 0 || numberOfPepperoniTopping < 6)) {            this.numberOfPepperoniTopping=numberOfPepperoniTopping;        } else if (size.equalsIgnoreCase("large") && (numberOfPepperoniTopping >= 0 || numberOfPepperoniTopping < 7)) {            this.numberOfPepperoniTopping = numberOfPepperoniTopping;        } else {            System.out.println("Invalid number of pepperoni toping:" + numberOfPepperoniTopping);            System.exit(0);        }        this.numberOfPepperoniTopping = numberOfPepperoniTopping;    }    public Pizza(String size, int numberOfCheeseTopping, int numberOfPepperoniTopping) {        setSize(size);        setNumberOfCheeseTopping(numberOfCheeseTopping);        setNumberOfPepperoniTopping(numberOfPepperoniTopping);    }    public double calcCost() {        double startingPrice = (size.equalsIgnoreCase("small")) ? 10 : (size.equalsIgnoreCase("Medium")) ? 12 : 14;        double totalPrice=startingPrice+2*(numberOfPepperoniTopping+numberOfCheeseTopping);        return totalPrice;    }    public String toString() {        return "Pizza{" +                "size='" + size + '\'' +                ", numberOfCheeseTopping=" + numberOfCheeseTopping +                ", numberOfPepperoniTopping=" + numberOfPepperoniTopping +                ", Total cost=" + calcCost() +                '}';    }}/*5. Pizza Task:		5.1 Create class named Pizza:				private variables:					size, numberOfCheeseTopping, numberOfPepperoniTopping				Encapsulate all the fields						Conditions:							size of the pizza can only be small, medium, large. case insensitive							Number of cheese topping can not be negative, the maximum number of cheese topping is:										small: 3										medium: 4										large: 5							Number of pepperoni topping can not be negative, the maximum number of pepperoni topping is:										small: 4										medium: 5										large: 6				Add a constructor that allows user to set all the fields when the object is created.								(If the arguments not valid it should not be set to the instances)				Methods:					calcCost(): returns the totalCost of the pizza					toString():returns a String containing the pizza size, quantity of each topping, and the pizza cost as calculated by calcCost()		Pizza cost is determined by:                        S: $10 + $2 per topping                        M: $12 + $2 per topping                        L: $14 + $2 per topping*/