package day35.Task2;public class BankAccount {    private String accountHolder;    private int accountNumber, balance;    public BankAccount(String accountHolder, int accountNumber, int balance) {        this.accountHolder = accountHolder;        this.accountNumber = accountNumber;        this.balance = balance;    }    public String getAccountHolder() {        return accountHolder;    }    public void setAccountHolder(String accountHolder) {        this.accountHolder = accountHolder;    }    public int getAccountNumber() {        return accountNumber;    }    public void setAccountNumber(int accountNumber) {        this.accountNumber = accountNumber;    }    public int getBalance() {        return balance;    }    public void setBalance(int balance) {        this.balance = balance;    }    public void deposit(double amount) {        if (amount <= 0) {            System.out.println("Depos't amount cannot be negative or zero");            System.exit(0);        }        System.out.println("Deposit " + amount + "  to the account " + accountNumber);        balance += amount;    }    public void withdraw(double amount){        if (amount<=0){            System.out.println("Invalid amount of witdrawal");            System.exit(0);        }        if (amount>balance){            System.out.println("Unsufficiant balance"+balance);            System.exit(0);        }        System.out.println("Witdrawed monay is:"+amount);        balance-=amount;    }    public void checkBalance(){        System.out.println(balance);    }    public String toString() {        return "BankAccount{" +                "accountHolder='" + accountHolder + '\'' +                ", accountNumber=" + accountNumber +                ", balance=" + balance +                '}';    }}/*3. create a class named BankAccount        Private variables:            accountHolder, accountNumber, balance        encapsulate all the fields        Add a constructor that allows user to set all the fields when the object is created.                        (If the arguments not valid it should not be set to the instances)        Extra methods:                deposit()                withdraw()                checkbalance()                toString()            DO NOT duplicate any code fragments*/