package day36.employeeTask;public class Employee {    private String name;    private char gender;    private int age, id;    private String jobTitle;    private double salary;    public void setInfo(String name, char gender, int age, int id, String jobTitle, double salary) {        setName(name);        setGender(gender);        setId(id);        setJobTitle(jobTitle);        setSalary(salary);    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public char getGender() {        return gender;    }    public void setGender(char gender) {        this.gender = gender;    }    public int getAge() {        return age;    }    public void setAge(int age) {        this.age = age;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getJobTitle() {        return jobTitle;    }    public void setJobTitle(String jobTitle) {        this.jobTitle = jobTitle;    }    public double getSalary() {        return salary;    }    public void setSalary(double salary) {        this.salary = salary;    }    public void work(){        System.out.println(getName()+" is working");    }    public String toString(){        return    '{' +"name="+name+                ",gender="+gender+                ",age="+age+                ",id="+id+                ",jobTitle="+jobTitle+                ",salary="+salary+'}' ;    }}/*2. Employee Task:	2.1 Create a class named Employee:			variables:				name, gender, age, id, jobTitle, salary			Methods:				setInfo()				work()				toString()*/