package day36.sportsTask;public class Football extends Sport {    private int numberOfPlayers;    public void setInfo(String name, int numberOfReferre, String rule,int numberOfPlayers) {        setInfo(name, numberOfReferre, rule);        setNumberOfPlayers(numberOfPlayers);    }    public int getNumberOfPlayers() {        return numberOfPlayers;    }    public void setNumberOfPlayers(int numberOfPlayers) {        if (!(numberOfPlayers==11)){            System.out.println("Football team must be 11");            System.exit(0);        }        this.numberOfPlayers = numberOfPlayers;    }    public String toString() {        return "Football{" +                "Name=" + '\''+getName() + '\''+                "numberOfReferre=" +'\''+ getNumberOfReferre()+ '\'' +                " Rules : " + getRules() +'\''+                "numberOfPlayers=" + numberOfPlayers +                '}';    }}