package day36.sportsTask;public class Sport {    private String name;    private int numberOfReferre;    private String rules;    public void setInfo(String name,  int numberOfReferre, String rules) {        setName(name);        setNumberOfReferre(numberOfReferre);        setRules(rules);    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getNumberOfReferre() {        return numberOfReferre;    }    public void setNumberOfReferre(int numberOfReferre) {        if (numberOfReferre < 1) {            System.out.println("Necessary min 1 referre");            System.exit(0);        }        this.numberOfReferre = numberOfReferre;    }    public String getRules() {        return rules;    }    public void setRules(String rules) {        this.rules = rules;    }    public void play() {        System.out.println("Team is playing " + getName());    }    public String toString() {        return "Sport{" +                "name='" + name + '\'' +                ", numberOfReferre=" + numberOfReferre +                ", rules='" + rules + '\'' +                '}';    }}/*Sports Task:	1. Create a class named Sport			variables:				name, numberOfPlayers, numberOfReferre, rules			methods:				setInfo()				play()				toString()		2. create the following sub classes of Sport:				1. Basketball				2. Baseball				3. Football				4. AmericanFootball			Add any missing variables or methods			Note: you can search them on google*/